package com.wang.algorism.leetcode;

import java.util.*;

public class CheckInclusion {

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(System.currentTimeMillis());
        System.out.println(solution.checkInclusion(""
                , ""));
        //System.out.println(solution.checkInclusion("a1", "1ac"));
        System.out.println(System.currentTimeMillis());
    }
    public static class Solution {

        public boolean checkInclusion(String s1, String s2) {
            for (int i = 0; i < s2.length() - s1.length() + 1; i++) {
                String substring = s2.substring(i, i + s1.length());
                if(listEquals(s1, substring)){
                    return true;
                }
            }
            return false;
        }

        public boolean listEquals(String a1, String a2){
            int[] a1Map = new int[26];
            int[] a2Map = new int[26];
            for (int i = 0; i < a1.length(); i++) {
                char c = a1.charAt(i);
                a1Map[c-'a']++;
            }
            for (int i = 0; i < a2.length(); i++) {
                char c = a2.charAt(i);
                a2Map[c-'a']++;
            }
            boolean equals = true;
            for (int i = 0; i < 26; i++) {
                if(a1Map[i] != a2Map[i]){
                    equals = false;
                }
            }
            return equals;
        }
    }
}
